mapper.select:
SELECT	COUNT(*) CNT
FROM	MENU
WHERE	MENU_NO = #{code}

mapper.insert:
		INSERT
		INTO	CODE
		(
			CODE_NO
		,	CODE_NAME
        ,   UPPER_CODE_NO
        ,	USE_YN
        ,	CODE_VAL
        ,	CODE_SORT
        ,	INSERT_DATE
        ,	UPDATE_DATE
		)
		VALUES
		(
			#{code}
		,	#{codeName}
		,	#{upperCode}
		,	#{useyn}
		,	#{codeVal}
		,	#{codeSort}
		,	SYSDATE
		,	SYSDATE
		)

mapper.update:
UPDATE	CODE
SET		CODE_NAME	= #{codeName}
	,   USE_YN		= #{useyn}
	,	CODE_VAL	= #{codeVal}
	,	CODE_SORT	= #{codeSort}
	,	UPDATE_DATE	= SYSDATE
WHERE	CODE_NO 	= #{code}
